(3, '<p><span style=\"font-size:16px\">Amritsari Chicken Masala Recipe</span></p>', '<ul>\r\n	<li><span style=\"font-size:14px\">500 gms chicken</span></li>\r\n	<li><span style=\"font-size:14px\">2 tsp ginger garlic Paste</span></li>\r\n	<li><span style=\"font-size:14px\">3 tsp curd</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp lemon Juice</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp vinegar</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp coriander powder</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp cumin powder</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp red chilli powder</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp salt</span></li>\r\n	<li><span style=\"font-size:14px\">2 tsp onion, chopped</span></li>\r\n	<li><span style=\"font-size:14px\"><strong>For Gravy:</strong></span></li>\r\n	<li><span style=\"font-size:14px\">2 tsp butter</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp red chilli powder</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp coriander powder</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp cumin powder, chopped</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp ginger</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 cup water</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp salt</span></li>\r\n	<li><span style=\"font-size:14px\">1 Green chilli</span></li>\r\n	<li><span style=\"font-size:14px\">6 Tomatoes</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 tsp sugar</span></li>\r\n	<li><span style=\"font-size:14px\">3 tsp butter</span></li>\r\n	<li><span style=\"font-size:14px\">3 tsp cream</span></li>\r\n</ul>', 'Amritsari masala chicken.jpg', '<p><span style=\"font-size:14px\"><strong>For marination:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">1.Take chicken in big bowl.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">2.Add ginger-garlic paste, curd, lemon juice, vinegar, coriander powder, cumin powder, red chilli powder, salt and chopped onion to the chicken.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">3.Mix all the ingredients thoroughly.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">4.Keep aside for marination for 2 hours.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>For gravy:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">1.Take butter in a hot pan, add red chilli powder to it.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">2.Saute them in a pan.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">3.Add coriander powder, cumin powder and chopped ginger to it.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">4.Saute them well.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">5.Add water to the pan and mix well.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">6.Add salt, green chilli, tomato and sugar. Mix well.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">7.Take butter in another hot pan and spread it all along the sides of the pan.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">8.Add the marinated chicken into the pan.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">9.Saute the chicken with the butter.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">10.Cover the pan to let the chicken cook.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">11.Remove the lid and check if the chicken has turned golden brown and the butter has subsided.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">12.Add the cooked tomato gravy to the chicken and mix well.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">13.Cover the pan again and let it cook for a while.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">14.Now remove the lid and add cream to the gravy.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">15.Mix them well.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">16.Serve hot, topped with butter, coriander leaves and green chillies.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Key Ingredients:&nbsp;</strong>chicken, ginger garlic paste, curd, lemon juice, vinegar, coriander powder, cumin powder, red chilli powder, salt, onion, butter, red chilli powder, coriander powder, cumin powder, ginger, water, salt, Green chilli, Tomatoes, sugar, butter, cream.</span></p>', 'active', '2021-01-20 07:03:32', '2021-01-21 01:25:20'),
(4, '<p><span style=\"font-size:16px\">Spicy Chicken Recipe</span></p>', '<ul>\r\n	<li>75 Ml ustard oil</li>\r\n	<li>1 tsp onion seeds (kalonjee)</li>\r\n	<li>1 tsp fennel seeds (saunf)</li>\r\n	<li>2 tbsp garlic, chopped</li>\r\n	<li>200 gms onion, chopped</li>\r\n	<li>150 gms tomatoes, chopped</li>\r\n	<li>1 tsp ginger, chopped</li>\r\n	<li>2-3 Green chillies</li>\r\n	<li>2 tbsp curry leaves</li>\r\n	<li>1 kg chicken leg (cubes, boneless)</li>\r\n	<li>1 tbsp tomato paste</li>\r\n	<li>1 tbsp red chilli powder</li>\r\n	<li>1 tsp black pepper (crushed)</li>\r\n	<li>1 tsp cumin powder, roasted</li>\r\n	<li>to taste Salt</li>\r\n	<li>A pinch of black salt</li>\r\n	<li>20 gms fresh coriander</li>\r\n	<li>10 gms mint</li>\r\n</ul>', 'spicy chicken.jpg', '<ul>\r\n	<li>1.Heat mustard oil. Add onion seeds and fennel seeds. Once they release flavor, add chopped garlic. Saute for a while.</li>\r\n	<li>2.Add the chopped onion, tomato, ginger and green chillies. Stir fry until the onion turns brownish.</li>\r\n	<li>3.Meanwhile, fry curry leaves and crush them.</li>\r\n	<li>4.Add the boneless chicken cubes, tomato paste, and stir for a while.</li>\r\n	<li>5.Add the other dry spices and mix well.</li>\r\n	<li>6.Cook on a low flame, with a lid on top for about 10 minutes.</li>\r\n	<li>7.Add the crushed curry leaves and mix well.</li>\r\n	<li>8.Serve the dish garnished with fresh coriander and mint.</li>\r\n</ul>', 'active', '2021-01-20 07:14:31', '2021-01-21 01:00:51'),
(5, '<p><span style=\"font-size:16px\">Kerala Chicken Roast Recipe</span></p>', '<ul>\r\n	<li>1 Kg chicken</li>\r\n	<li><strong>For the marination:</strong></li>\r\n	<li>1/4 tsp turmeric powder</li>\r\n	<li>1/4 tsp chilli powder</li>\r\n	<li>1/4 tsp coriander powder</li>\r\n	<li>1 tsp vinegar/lemon juice</li>\r\n	<li>5 Garlic cloves</li>\r\n	<li>3-5 Green chillies</li>\r\n	<li>to taste salt</li>\r\n	<li><strong>For roasting:</strong></li>\r\n	<li>3 medium onions</li>\r\n	<li>1 large tomato (remove skin &amp; make puree), blanched</li>\r\n	<li>2 Green chillies (slit)</li>\r\n	<li>3 sprigs curry leaves</li>\r\n	<li>1/4 tsp garam masala powder</li>\r\n	<li>1/4 tsp pepper powder</li>\r\n	<li>1 Lemon (juiced)</li>\r\n	<li>Oil</li>\r\n</ul>', 'Kerala chicken roast image.jpg', '<p>1.Clean and cut the chicken into medium sized pieces.</p>\r\n\r\n<p>2.Make a paste of all the ingredients under marination.</p>\r\n\r\n<p>3.Marinate the chicken pieces with this paste and keep aside for an hour.</p>\r\n\r\n<p>4.Cut onions into very thin slices and fry in hot oil. Drain them and keep aside.</p>\r\n\r\n<p>5.Fry the chicken pieces in the same oil until almost done. Keep the pan covered so that the chicken does not get browned, but is cooked inside. Drain them and keep aside.</p>\r\n\r\n<p>6.Heat 2 tbsp of oil in another pan (You can use the same oil in which we fried the chicken pieces. This will enhance the flavour). Add slit green chillies and curry leaves. Saute for&nbsp; &nbsp; &nbsp; &nbsp;1minute.</p>\r\n\r\n<p>7.Add the tomato paste to this and saute for 1 minute.</p>\r\n\r\n<p>8.Now add the prepared chicken &amp; roast until it is dry.</p>\r\n\r\n<p>9.Make sure that all the chicken pieces are well coated with the tomato paste.</p>\r\n\r\n<p>10.Sprinkle garam masala &amp; pepper powder and mix well.</p>\r\n\r\n<p>11.Add lemon juice and mix well. Check for salt. Add a little if needed.</p>\r\n\r\n<p>12.Now add the fried onions and mix well.</p>\r\n\r\n<p>13.Tasty Kerala Chicken roast is ready.</p>', 'active', '2021-01-20 07:22:02', '2021-01-21 01:01:05'),
(6, '<p><span style=\"font-size:16px\">Masaledar Chicken Lollipop Recipe</span></p>', '<ul>\r\n	<li><strong>For the masala:</strong></li>\r\n	<li>8 Guntur chillies</li>\r\n	<li>6-7 Boriya mirch</li>\r\n	<li>1 inch ginger knob, finely chopped</li>\r\n	<li>5-6 Garlic cloves</li>\r\n	<li>2 Green chillies</li>\r\n	<li>2 tsp cumin</li>\r\n	<li>1 tsp fennel</li>\r\n	<li>5-6 peppercorns</li>\r\n	<li>1 Cinnamon stick</li>\r\n	<li>A pinch of white pepper</li>\r\n	<li>4 Cloves</li>\r\n	<li>15 Curry leaves</li>\r\n	<li>1 tsp coriander seeds</li>\r\n	<li>Handful of coriander leaves</li>\r\n	<li>1 tsp turmeric</li>\r\n	<li>A pinch of sea salt</li>\r\n	<li>1-2 tsp oil</li>\r\n	<li><strong>For the chicken:</strong></li>\r\n	<li>8-10 Chicken lollipops (with the skin)</li>\r\n	<li>1 tsp turmeric</li>\r\n	<li>to taste salt</li>\r\n	<li>1-2 tsp red chilli powder</li>\r\n	<li>2 tsp oil + oil to sear the lollipops</li>\r\n	<li>A pinch of sugar</li>\r\n	<li><strong>For the base:</strong></li>\r\n	<li>2-3 tsp oil</li>\r\n	<li>2 tsp mustard seeds</li>\r\n	<li>7-8 Curry leaves</li>\r\n	<li>1/2 onion, julienne</li>\r\n	<li>1 1/2 tbsp masala</li>\r\n	<li>3 tsp tamarind paste</li>\r\n	<li>1 tsp jaggery powder</li>\r\n	<li>1/2 Lemon (juiced)</li>\r\n	<li>for garnishing coriander leaves</li>\r\n</ul>', 'Masaledaar chicken lollipop image.webp', '<ul>\r\n	<li><strong>Prepare masala:</strong></li>\r\n	<li><em>1.</em>Dry roast all the ingredients together in a pan for a minute.</li>\r\n	<li><em>2.</em>In a mortar and pestle pound together all the ingredients.</li>\r\n	<li><em>3.</em>Transfer this in the blender and make a fine paste.</li>\r\n	<li><strong>Prepare chicken:</strong></li>\r\n	<li><em>1.</em>Marinate the chicken lollipops with turmeric, salt, red chilli powder and 2 tsp oil.</li>\r\n	<li><em>2.</em>In a saucepan add a little oil and sear the lollipops from all sides till they are half cooked.</li>\r\n	<li><em>3.</em>Add sugar and cook some more.</li>\r\n	<li><strong>Prepare the base:</strong></li>\r\n	<li><em>1.</em>Add refined oil in the pan followed with mustard seeds, few curry leaves and onion. Bhuno them well together.</li>\r\n	<li><em>2.</em>Add the masala and chicken lollipop into the base and saute them well together.</li>\r\n	<li><em>3.</em>Add tamarind extract, jaggery and water. Mix them well and cover the top.</li>\r\n	<li><em>4.</em>Add little lemon. Toss well and cook till chicken is well done.</li>\r\n</ul>', 'active', '2021-01-20 07:27:25', '2021-01-21 01:01:15'),
(7, '<h1><span style=\"font-size:14px\">How to make Butter Chicken Recipe</span></h1>', '<ul>\r\n	<li>400 gms raw chicken</li>\r\n	<li><strong>For the Marinade:</strong></li>\r\n	<li>2 tsp red chilli powder</li>\r\n	<li>2 tsp ginger and garlic paste</li>\r\n	<li>2 tsp salt</li>\r\n	<li>2 tsp lemon juice</li>\r\n	<li>1/2 cup curd</li>\r\n	<li>1/2 tsp garam masala</li>\r\n	<li>1 tsp kasuri methi</li>\r\n	<li>2 tsp mustard oil</li>\r\n	<li><strong>For gravy:</strong></li>\r\n	<li>2 tsp oil</li>\r\n	<li>2 to taste butter cubes</li>\r\n	<li>3 gram cloves</li>\r\n	<li>1 Cinnamon sticks, sliced</li>\r\n	<li>1 tsp mace</li>\r\n	<li>7 Cardamom</li>\r\n	<li>4 Tomatoes, chopped</li>\r\n	<li>1 tsp garlic</li>\r\n	<li>1 tsp ginger</li>\r\n	<li>2 butter cubes</li>\r\n	<li>1 tsp ginger garlic paste</li>\r\n	<li>1 1/2 tsp red chilli powder</li>\r\n	<li>1 tsp kasuri methi</li>\r\n	<li>2 tsp honey</li>\r\n	<li>1 green chilli</li>\r\n	<li>2 tsp cardamom powder</li>\r\n	<li>1 tbsp cream</li>\r\n</ul>', 'Butter chicken.jpg', '<ul>\r\n	<li><strong>For marination:</strong></li>\r\n	<li>1.In a mixing bowl, put raw chicken pieces and add salt, red chilli powder, ginger garlic paste and lemon juice. Mix well.</li>\r\n	<li>2.Refrigerate for about 15-20 minutes.</li>\r\n	<li>3.Now add curd to the refrigerated mix. Followed by salt, ginger garlic paste, red chilli powder, garam masala, kasuri methi and mustard oil. Mix well and refrigerate again for an hour.</li>\r\n	<li>4.Roast the marinated chicken in an oven for about 30 minutes until it is three-fourth done.</li>\r\n	<li><strong>Prepare the chicken gravy:</strong></li>\r\n	<li>1.Heat 2 tsp of oil in a pan with butter.</li>\r\n	<li>2.Add cloves, cinnamon stick, mace and cardamom. Saute and then add chopped tomatoes, garlic and ginger. Mix well and then grind well.</li>\r\n	<li>3.In another pan, heat another two cubes of butter, along with ginger garlic paste.</li>\r\n	<li>4.Add the tomato puree made from the mixture. Now add red chilli powder, kasuri methi, honey and finally the roasted chicken pieces. Let it simmer.</li>\r\n	<li>5.Add green chilli, cardamom powder and cream. Mix well.</li>\r\n	<li>6.Serve with a teaspoon of cream over.</li>\r\n</ul>\r\n\r\n<p><strong>Key Ingredients:&nbsp;</strong>raw chicken, red chilli powder, ginger and garlic paste, salt, lemon juice, curd, garam masala , kasuri methi , mustard oil , oil, butter cubes, cloves, Cinnamon sticks, mace, Cardamom, Tomatoes, garlic, ginger, butter cubes, ginger garlic paste, red chilli powder, kasuri methi, honey, green chilli, cardamom powder, cream</p>', 'active', '2021-01-19 19:30:41', '2021-01-19 19:31:45'),
(8, '<h2><span style=\"font-size:14px\">How to Make Chicken Dum Biryani</span></h2>', '<ul>\r\n	<li>500 Gram chicken legs</li>\r\n	<li><strong>For the marinade:</strong></li>\r\n	<li>1 tsp red chilli powder</li>\r\n	<li>2 tsp ginger garllic paste</li>\r\n	<li>1/2 cup yogurt</li>\r\n	<li>1/2 tsp turmeric pwoder</li>\r\n	<li>1 tsp coriander powder</li>\r\n	<li>1 tsp garam masala powder</li>\r\n	<li>1 1/2 tsp salt</li>\r\n	<li><strong>For the rice:</strong></li>\r\n	<li>A pinch of saffron</li>\r\n	<li>1/2 cup milk</li>\r\n	<li>2 cups water</li>\r\n	<li>5 cardamoms</li>\r\n	<li>3 cloves</li>\r\n	<li>1 stick cinnamon</li>\r\n	<li>2 mace</li>\r\n	<li>2 black cardamom</li>\r\n	<li>5 black peppercorn</li>\r\n	<li>1/2 tsp salt</li>\r\n	<li>1 cup rice (long grain, washed basmati rice)</li>\r\n	<li><strong>For the finishing:</strong></li>\r\n	<li>1 tbsp ghee</li>\r\n	<li>1 tbsp coriander leaves, chopped</li>\r\n	<li>2 tbsp fried onion</li>\r\n	<li>3 almonds, chopped</li>\r\n</ul>', 'Chicken dum biryani.jpg', '<ul>\r\n	<li>1.First mix the saffron in half a cup of milk and keep aside.</li>\r\n	<li>2.Now in a bowl, add all the marination ingredients and coat the chicken pieces well with it. Let it marinate for about 4 hours.</li>\r\n	<li>3.Now to prepare the rice, put all the ingredients given except saffron and milk, in warm water along with washed rice.</li>\r\n	<li>4.Cook for 10-15 minutes.</li>\r\n	<li>5.Heat some ghee in a pan and add all the marinated chicken followed by cooked rice in it. Top with saffron milk, coriander leaves, fried onion and chopped almonds. Mix well.</li>\r\n	<li>6.Let it cook on a low heat (on dum, in it&#39;s own heat) covered with foil for about 20-30 minutes (till the water evaporates and chicken and rice are fully cooked).</li>\r\n	<li>7.Open the dum when cooked, mix the rice well. You can drizzle with some rose water and serve hot.</li>\r\n</ul>', 'active', '2021-01-19 19:33:40', '2021-01-19 19:33:40'),
(9, '<p><span style=\"font-size:16px\">Awadhi Mutton Biryani Recipe</span></p>', '<ul>\r\n	<li><span style=\"font-size:14px\"><strong>For garam masala:</strong></span></li>\r\n	<li><span style=\"font-size:14px\">1 Cinnamon stick</span></li>\r\n	<li><span style=\"font-size:14px\">8-10 Cloves</span></li>\r\n	<li><span style=\"font-size:14px\">2-3 tsp cumin seeds</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp fennel seeds</span></li>\r\n	<li><span style=\"font-size:14px\">2-3 tsp coriander seeds</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp pepper corns</span></li>\r\n	<li><span style=\"font-size:14px\">2 Star anise</span></li>\r\n	<li><span style=\"font-size:14px\">2-3 Mace</span></li>\r\n	<li><span style=\"font-size:14px\">2-3 Brown cardamom</span></li>\r\n	<li><span style=\"font-size:14px\">3-4 Green cardamom</span></li>\r\n	<li><span style=\"font-size:14px\"><strong>For mutton marination:</strong></span></li>\r\n	<li><span style=\"font-size:14px\">1/2 kg mutton</span></li>\r\n	<li><span style=\"font-size:14px\">2-3 tsp ginger-garlic paste</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp turmeric</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp chilli powder</span></li>\r\n	<li><span style=\"font-size:14px\">Cashew nut paste</span></li>\r\n	<li><span style=\"font-size:14px\">A pinch of garam masala</span></li>\r\n	<li><span style=\"font-size:14px\">4-5 tsp Curd</span></li>\r\n	<li><span style=\"font-size:14px\"><strong>For cooking:</strong></span></li>\r\n	<li><span style=\"font-size:14px\">2-3 tsp salt</span></li>\r\n	<li><span style=\"font-size:14px\">3 tsp ghee</span></li>\r\n	<li><span style=\"font-size:14px\">2-3 tbsp oil</span></li>\r\n	<li><span style=\"font-size:14px\">2-3 cups milk</span></li>\r\n	<li><span style=\"font-size:14px\">Saffron</span></li>\r\n</ul>', 'Mutton biryani.jpg', '<ul>\r\n	<li><span style=\"font-size:14px\"><strong>Prepare garam masala:</strong></span></li>\r\n	<li><span style=\"font-size:14px\"><em>1.</em>Dry roast all the spices.</span></li>\r\n	<li><span style=\"font-size:14px\"><em>2.</em>Once they are roasted, transfer them to a masala grinder and grind them finely.</span></li>\r\n	<li><span style=\"font-size:14px\"><strong>Prepare mutton marination:</strong></span></li>\r\n	<li><span style=\"font-size:14px\"><em>1.</em>To half kg mutton, add ginger-garlic paste, turmeric and chilli powder.</span></li>\r\n	<li><span style=\"font-size:14px\"><em>2.</em>Then add cashew nut paste, garam masala, curd and whisk it.</span></li>\r\n	<li><span style=\"font-size:14px\"><em>3.</em>Cover it with the lid and put it in the refrigerator for an hour.</span></li>\r\n	<li><span style=\"font-size:14px\"><strong>Final Preparation:</strong></span></li>\r\n	<li><span style=\"font-size:14px\"><em>1.</em>Let the meat come to room temperature. Season the meat with salt.</span></li>\r\n	<li><span style=\"font-size:14px\"><em>2.</em>Grease the handi with some ghee and oil. Transfer the marinated meat from the bowl to the handi.</span></li>\r\n	<li><span style=\"font-size:14px\"><em>3.</em>Now stir and cook the meat for a few minutes.</span></li>\r\n	<li><span style=\"font-size:14px\"><em>4.</em>Cover with the lid and simmer it for another half an hour.</span></li>\r\n	<li><span style=\"font-size:14px\"><em>5.</em>Now layer the mutton with cooked rice and pour a little saffron induced milk over it.</span></li>\r\n	<li><span style=\"font-size:14px\"><em>6.</em>Add a little salt, garam masala, roasted onions and ghee over it.</span></li>\r\n	<li><span style=\"font-size:14px\"><em>7.</em>Cover the handi with the lid and weight it down with something heavy. Keep the flame low.</span></li>\r\n	<li><span style=\"font-size:14px\"><em>8.</em>Cook for about half an hour. Serve it hot.</span></li>\r\n</ul>', 'active', '2021-01-21 01:07:23', '2021-01-21 01:36:42'),
(10, '<p><span style=\"font-size:16px\">Kosha Mangsho Recipe&nbsp;</span></p>', '<ul>\r\n	<li><span style=\"font-size:14px\">500 Gram Mutton (cut into pieces)</span></li>\r\n	<li><span style=\"font-size:14px\">1/4 cup Mustard oil</span></li>\r\n	<li><span style=\"font-size:14px\">4 pieces Cloves</span></li>\r\n	<li><span style=\"font-size:14px\">Green cardamoms and some tiny pieces of cinnamon</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 cup Onions, grated</span></li>\r\n	<li><span style=\"font-size:14px\">1 tbsp Ginger-garlic paste</span></li>\r\n	<li><span style=\"font-size:14px\">to taste Chilli powder</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 tbsp Turmeric</span></li>\r\n	<li><span style=\"font-size:14px\">1 tbsp Cumin seeds (ground), roasted</span></li>\r\n	<li><span style=\"font-size:14px\">500 gram Hung curd</span></li>\r\n	<li><span style=\"font-size:14px\">1 1/2 tbsp Salt</span></li>\r\n</ul>', 'Kosha mangsho.gif', '<ul>\r\n	<li><span style=\"font-size:14px\">1.Heat oil and add cloves, cardamom and cinnamon.</span></li>\r\n	<li><span style=\"font-size:14px\">2.Add onions and saute till soft.</span></li>\r\n	<li><span style=\"font-size:14px\">3.Add ginger-garlic paste, chilli powder and turmeric and stir fry.</span></li>\r\n	<li><span style=\"font-size:14px\">4.Add meat, stir fry over high heat till opaque.</span></li>\r\n	<li><span style=\"font-size:14px\">5.Lower the heat and saute till fat separates.</span></li>\r\n	<li><span style=\"font-size:14px\">6.Add curd, cumin and salt and saute till fat separates again.</span></li>\r\n	<li><span style=\"font-size:14px\">7.Cover tight and cook over very low heat, till it gets cooked.</span></li>\r\n	<li><span style=\"font-size:14px\">8.Serve hot.c</span></li>\r\n</ul>', 'active', '2021-01-21 01:09:57', '2021-01-21 01:35:58'),
(11, '<p><span style=\"font-size:16px\">Bhindiwala Meat Recipe</span></p>', '<ul>\r\n	<li><span style=\"font-size:14px\">6 Tbsp Oil</span></li>\r\n	<li><span style=\"font-size:14px\">1 1/2 Cup Okra (cleaned)</span></li>\r\n	<li><span style=\"font-size:14px\">1 1/2 Large Onions, grated</span></li>\r\n	<li><span style=\"font-size:14px\">4 Large Tomatoes, grated</span></li>\r\n	<li><span style=\"font-size:14px\">1 Tbsp Ginger and garlic paste</span></li>\r\n	<li><span style=\"font-size:14px\">3 tsp Salt</span></li>\r\n	<li><span style=\"font-size:14px\">2 (heaped) tsp Coriander powder</span></li>\r\n	<li><span style=\"font-size:14px\">2 (heaped) tsp Garam masala</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp Cayenne/red chilli powder</span></li>\r\n	<li><span style=\"font-size:14px\">2 Sticks Cinnamon</span></li>\r\n	<li><span style=\"font-size:14px\">2 Large Black cardamom</span></li>\r\n	<li><span style=\"font-size:14px\">12 Cloves</span></li>\r\n	<li><span style=\"font-size:14px\">3/4 Kg Meat (boneless)</span></li>\r\n</ul>', 'Bhindiwala-Meat.jpg', '<ul>\r\n	<li><span style=\"font-size:14px\">1.Put oil in pan and add onions. Cook onions till translucent. Add the cinnamon, cloves, bay leaf and cardamom.</span></li>\r\n	<li><span style=\"font-size:14px\">2.Stir in meat and then salt.</span></li>\r\n	<li><span style=\"font-size:14px\">3.Cook for 10 minutes and add all the spices. Add tomatoes and keep cooking and stirring (bhuno). Let the oil separate.</span></li>\r\n	<li><span style=\"font-size:14px\">4.Add 6 cups of water and transfer it into a pressure cooker. Add 2 heaped teaspoons of garam masala and cook on fast flame till it whistles.</span></li>\r\n	<li><span style=\"font-size:14px\">5.Lower the flame and cook for 25 minutes. Now add the okra. Cook till the okra is soft.</span></li>\r\n	<li><span style=\"font-size:14px\">6.Once the okra is done, it indicates that the meat is done too.</span></li>\r\n	<li><span style=\"font-size:14px\">7.Serve.</span></li>\r\n</ul>', 'active', '2021-01-21 01:12:20', '2021-01-21 01:36:22'),
(12, '<p><span style=\"font-size:14px\">Parsi Mutton Cutlets Recipe</span></p>', '<ul>\r\n	<li><span style=\"font-size:14px\">250 gms mutton mince</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp ginger paste</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp garlic paste</span></li>\r\n	<li><span style=\"font-size:14px\">1 Green chilli (coarsely ground)</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 tsp turmeric</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp red chilli powder</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 tsp clove-cinnamon powder</span></li>\r\n	<li><span style=\"font-size:14px\">2 tbsp potatoes (mashed), boiled</span></li>\r\n	<li><span style=\"font-size:14px\">5-6 pudina leaves, finely chopped</span></li>\r\n	<li><span style=\"font-size:14px\">1 tbsp coriander, chopped</span></li>\r\n	<li><span style=\"font-size:14px\">to taste salt</span></li>\r\n	<li><span style=\"font-size:14px\">Bread crumbs (to coat)</span></li>\r\n	<li><span style=\"font-size:14px\">4 Eggs (whisked, to coat the cutlets)</span></li>\r\n	<li><span style=\"font-size:14px\">Oil (to deep fry)</span></li>\r\n</ul>', 'MUtton keema recipe.jpg', '<ul>\r\n	<li><span style=\"font-size:14px\">1.In a bowl take the mince and massage it with your hands for 3-4 minutes.</span></li>\r\n	<li><span style=\"font-size:14px\">2.Now add the ginger paste, garlic paste, green chilli, turmeric, red chilli powder, clove-cinnamon powder, mashed potato, pudina, dhaniya and salt. Mix it well.</span></li>\r\n	<li><span style=\"font-size:14px\">3.Make medium sized round patties (makes around 5-6). Coat the cutlets with bread crumbs from both sides. Refrigerate for 30 minutes.</span></li>\r\n	<li><span style=\"font-size:14px\">4.Whisk eggs. Coat the refrigerated cutlets in the egg mixture. Deep fry in hot oil till golden brown from both sides.</span></li>\r\n	<li><span style=\"font-size:14px\">5.Serve with sliced onions and chutney. Squeeze some lemon juice on top if you want.</span></li>\r\n</ul>', 'active', '2021-01-21 01:15:00', '2021-01-21 01:36:54'),
(13, '<p><span style=\"font-size:16px\">Masala Fried Pomfret Recipe</span></p>', '<ul>\r\n	<li><span style=\"font-size:14px\"><strong>For the fish:</strong></span></li>\r\n	<li><span style=\"font-size:14px\">1 Pomfret</span></li>\r\n	<li><span style=\"font-size:14px\">To coat Semolina</span></li>\r\n	<li><span style=\"font-size:14px\">To shallow fry Oil</span></li>\r\n	<li><span style=\"font-size:14px\"><strong>For the masala:</strong></span></li>\r\n	<li><span style=\"font-size:14px\">2 tsp Ginger</span></li>\r\n	<li><span style=\"font-size:14px\">3 Garlic cloves</span></li>\r\n	<li><span style=\"font-size:14px\">1 tbsp Oil</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 tbsp Turmeric powder</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp Red chilli powder</span></li>\r\n	<li><span style=\"font-size:14px\">1 Lemon (juiced)</span></li>\r\n	<li><span style=\"font-size:14px\"><strong>For the warm cabbage and kiwi salad:</strong></span></li>\r\n	<li><span style=\"font-size:14px\">2 tbsp Oil</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 tsp Mustard seeds</span></li>\r\n	<li><span style=\"font-size:14px\">1 Red chilli (slit)</span></li>\r\n	<li><span style=\"font-size:14px\">1 Green chilli (slit)</span></li>\r\n	<li><span style=\"font-size:14px\">2-3 Curry leaves</span></li>\r\n	<li><span style=\"font-size:14px\">1 Spring onion (sliced diagonally)</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 cup Cabbage, shredded</span></li>\r\n	<li><span style=\"font-size:14px\">Salt</span></li>\r\n	<li><span style=\"font-size:14px\">Pepper</span></li>\r\n	<li><span style=\"font-size:14px\">1 Kiwi</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 Vinegar</span></li>\r\n	<li><span style=\"font-size:14px\">A pinch of Sugar</span></li>\r\n	<li><span style=\"font-size:14px\">To garnish Coconut , grated</span></li>\r\n</ul>', 'Masala fried pomfret.webp', '<ul>\r\n	<li><span style=\"font-size:14px\"><strong>Prepare the masala:</strong></span></li>\r\n	<li><span style=\"font-size:14px\">1.In a blender mix together ginger, garlic, oil, turmeric, red chilli and lemon juice. Masala is ready.</span></li>\r\n	<li><span style=\"font-size:14px\"><strong>Prepare the fish:</strong></span></li>\r\n	<li><span style=\"font-size:14px\">1.Make gashes in the pomfret and apply the masala. Marinate it for 15 minutes.</span></li>\r\n	<li><span style=\"font-size:14px\">2.Coat it in semolina from both sides and shallow fry till golden.</span></li>\r\n	<li><span style=\"font-size:14px\">3.Serve with warm cabbage and kiwi salad.</span></li>\r\n	<li><span style=\"font-size:14px\"><strong>Prepare the warm cabbage and kiwi salad:</strong></span></li>\r\n	<li><span style=\"font-size:14px\">1.In a non stick pan add oil, mustard seeds, red and green chilli, curry leaves. Let them splutter.</span></li>\r\n	<li><span style=\"font-size:14px\">2.Add spring onions. Saute.</span></li>\r\n	<li><span style=\"font-size:14px\">3.Add cabbage. Saute and add salt and pepper.</span></li>\r\n	<li><span style=\"font-size:14px\">4.Turn off the heat.</span></li>\r\n	<li><span style=\"font-size:14px\">5.Add diced kiwi, vinegar and sugar. Mix well and let it cook in the pan&#39;s residual heat.</span></li>\r\n	<li><span style=\"font-size:14px\">6.Garnish with grated coconut.</span></li>\r\n	<li><span style=\"font-size:14px\">7.Serve with masala fried fish.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Key Ingredients:&nbsp;</strong><a href=\"https://food.ndtv.com/ingredient/pomfret-701151\">Pomfret</a>,&nbsp;<a href=\"https://food.ndtv.com/ingredient/semolina-701156\">Semolina</a>, Oil,&nbsp;<a href=\"https://food.ndtv.com/ingredient/ginger-700888\">Ginger</a>, Garlic cloves, Oil, Turmeric powder, Red chilli powder, Lemon (juiced), Oil, Mustard seeds, Red chilli (slit), Green chilli (slit), Curry leaves, Spring onion (sliced diagonally),&nbsp;<a href=\"https://food.ndtv.com/ingredient/cabbage-700931\">Cabbage</a>,&nbsp;<a href=\"https://food.ndtv.com/ingredient/salt-701129\">Salt</a>, Pepper,&nbsp;<a href=\"https://food.ndtv.com/ingredient/kiwi-701225\">Kiwi</a>,&nbsp;<a href=\"https://food.ndtv.com/ingredient/vinegar-701179\">Vinegar</a>,&nbsp;<a href=\"https://food.ndtv.com/ingredient/sugar-700985\">Sugar</a>, Coconut</span></p>', 'active', '2021-01-21 01:23:48', '2021-01-21 01:37:06'),
(14, '<p><span style=\"font-size:16px\">Popcorn Crusted Fish Fingers Recipe</span></p>', '<ul>\r\n	<li><span style=\"font-size:14px\">250 gms fish (cut into fingers)</span></li>\r\n	<li><span style=\"font-size:14px\">1 Lemon (juiced)</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp worcestershire sauce</span></li>\r\n	<li><span style=\"font-size:14px\">to taste salt</span></li>\r\n	<li><span style=\"font-size:14px\">to taste pepper</span></li>\r\n	<li><span style=\"font-size:14px\">1 cup flour</span></li>\r\n	<li><span style=\"font-size:14px\">2 Egg white</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 cup bread crumbs</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 cup popcorn</span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>', 'Popcorn-Crusted-Fish-Fingers.jpg', '<ul>\r\n	<li><span style=\"font-size:14px\">1.Marinate the fish with lemon juice, worcestershire, salt and pepper for about 15 minutes.</span></li>\r\n	<li><span style=\"font-size:14px\">2.Roll in flour and dip in the egg white and finally in the popcorn, bread crumb mixture.</span></li>\r\n	<li><span style=\"font-size:14px\">3.Bake or deep fry in hot oil.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Key Ingredients:&nbsp;</strong>fish (cut into fingers), Lemon (juiced), worcestershire sauce, salt , pepper, flour, Egg white, bread crumbs, popcorn</span></p>', 'active', '2021-01-21 01:28:54', '2021-01-21 01:37:20'),
(15, '<p><span style=\"font-size:16px\">Fish Curry without Oil Recipe</span></p>', '<ul>\r\n	<li><span style=\"font-size:14px\">1/2 kg fresh fish</span></li>\r\n	<li><span style=\"font-size:14px\">1 bowl of tamarind juice</span></li>\r\n	<li><span style=\"font-size:14px\">Clay pot to cook</span></li>\r\n	<li><span style=\"font-size:14px\">1 big onion</span></li>\r\n	<li><span style=\"font-size:14px\">2 tomatoes</span></li>\r\n	<li><span style=\"font-size:14px\">3 tablespoons ground coconut</span></li>\r\n	<li><span style=\"font-size:14px\">2 green chillies</span></li>\r\n	<li><span style=\"font-size:14px\">1 teaspoon turmeric powder</span></li>\r\n	<li><span style=\"font-size:14px\">2 teaspoon red chilli powder</span></li>\r\n	<li><span style=\"font-size:14px\">Salt</span></li>\r\n	<li><span style=\"font-size:14px\">Water</span></li>\r\n	<li><span style=\"font-size:14px\">Curry leaves</span></li>\r\n	<li><span style=\"font-size:14px\">Ginger</span></li>\r\n</ul>', 'fish curry.jpg', '<ul>\r\n	<li><span style=\"font-size:14px\">1.Take chopped fish in a bowl and add tamarind juice to it and let it stay for 2-3 minutes.</span></li>\r\n	<li><span style=\"font-size:14px\">2.Heat the clay pot.</span></li>\r\n	<li><span style=\"font-size:14px\">3.Pour the fish in the clay pot and add chopped onion and tomatoes.</span></li>\r\n	<li><span style=\"font-size:14px\">4.Add grinned coconut mixed with turmeric powder.</span></li>\r\n	<li><span style=\"font-size:14px\">5.Then add green chillies and pour water and let it heat.</span></li>\r\n	<li><span style=\"font-size:14px\">6.Season with turmeric, chilli powder and salt.</span></li>\r\n	<li><span style=\"font-size:14px\">7.Add curry leaves and finely chopped ginger.</span></li>\r\n	<li><span style=\"font-size:14px\">8.Bring it to boil.</span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>', 'active', '2021-01-21 01:31:40', '2021-01-21 01:31:40'),
(16, '<p><span style=\"font-size:16px\">Fish Cutlets Recipe</span></p>', '<ul>\r\n	<li><span style=\"font-size:14px\"><strong>For the fish:</strong></span></li>\r\n	<li><span style=\"font-size:14px\">1 small ginger</span></li>\r\n	<li><span style=\"font-size:14px\">2 Green chillies</span></li>\r\n	<li><span style=\"font-size:14px\">3 Garlic cloves</span></li>\r\n	<li><span style=\"font-size:14px\">3-4 Rawa fillets</span></li>\r\n	<li><span style=\"font-size:14px\"><strong>For the base:</strong></span></li>\r\n	<li><span style=\"font-size:14px\">100 gms bread crumbs</span></li>\r\n	<li><span style=\"font-size:14px\">1 Egg</span></li>\r\n	<li><span style=\"font-size:14px\">2 Garlic cloves</span></li>\r\n	<li><span style=\"font-size:14px\">1 small ginger</span></li>\r\n	<li><span style=\"font-size:14px\">1 Chilli</span></li>\r\n	<li><span style=\"font-size:14px\">A few leaves of mint</span></li>\r\n	<li><span style=\"font-size:14px\">to taste salt</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp turmeric</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp red chilli powder</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp saunf</span></li>\r\n	<li><span style=\"font-size:14px\">4-5 Peppercorns</span></li>\r\n	<li><span style=\"font-size:14px\">1 Bread slice</span></li>\r\n	<li><span style=\"font-size:14px\">1 tsp oil</span></li>\r\n	<li><span style=\"font-size:14px\">Water</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 Lemon (juiced)</span></li>\r\n	<li><span style=\"font-size:14px\">1 potato (mashed), boiled</span></li>\r\n	<li><span style=\"font-size:14px\">2 tbsp flour (to coat)</span></li>\r\n	<li><span style=\"font-size:14px\">Oil (to pan fry)</span></li>\r\n	<li><span style=\"font-size:14px\">for garnishing mint leaves</span></li>\r\n</ul>', 'fish cutlets.jpg', '<ul>\r\n	<li><span style=\"font-size:14px\"><strong>Prepare the fish:</strong></span></li>\r\n	<li><span style=\"font-size:14px\"><em>1.</em>In a pan, steam the fish in a strainer with boiled water, ginger, green chilly, garlic and cover the pan.</span></li>\r\n	<li><span style=\"font-size:14px\"><strong>Prepare the base:</strong></span></li>\r\n	<li><span style=\"font-size:14px\"><em>1.</em>In a blender, mix bread crumbs, egg, garlic, ginger, green chillies, fresh mint leaves, salt, turmeric powder, red chili powder, slice of bread, refined oil and juice of half lime.</span></li>\r\n	<li><span style=\"font-size:14px\"><em>2.</em>In a mortar and pestle smash fennel and black pepper, add them into blender.</span></li>\r\n	<li><span style=\"font-size:14px\"><em>3.</em>Add a little water and blend them together.</span></li>\r\n	<li><span style=\"font-size:14px\"><em>4.</em>Add the steamed fish into the blender and grind them well.</span></li>\r\n	<li><span style=\"font-size:14px\"><em>5.</em>Transfer them into a bowl and add one boil potato for starch. Mix them well. Make cutlets out of it and coat in some flour.</span></li>\r\n	<li><span style=\"font-size:14px\"><em>6.</em>Keep it in the fridge for 10 minutes.</span></li>\r\n	<li><span style=\"font-size:14px\"><em>7.</em>Add little refined oil in the pan and fry the cutlets both sides.</span></li>\r\n	<li><span style=\"font-size:14px\"><em>8.</em>Garnish with mint leaves. Serve hot.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Key Ingredients:&nbsp;</strong>ginger, Green chillies, Garlic cloves, Rawa fillets, bread crumbs,&nbsp;<a href=\"https://food.ndtv.com/ingredient/egg-700905\">Egg</a>, Garlic cloves, ginger, Chilli, salt, turmeric, red chilli powder, saunf, Peppercorns, Bread slice, oil, Water, Lemon (juiced), potato (mashed), flour (to coat), Oil (to pan fry), mint leaves</span></p>', 'active', '2021-01-21 01:35:41', '2021-01-21 01:35:41'),
(17, '<p><span style=\"font-size:16px\">Baked Ham and Colby Sandwiches Recipe</span></p>', '<ul>\r\n	<li><span style=\"font-size:14px\">8 hamburger buns, split</span></li>\r\n	<li><span style=\"font-size:14px\">8 slices Colby cheese</span></li>\r\n	<li><span style=\"font-size:14px\">16 thin slices deli ham (about 1 pound)</span></li>\r\n	<li><span style=\"font-size:14px\">1-1/2 cups shredded part-skim mozzarella cheese</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 cup butter, melted</span></li>\r\n	<li><span style=\"font-size:14px\">2 tablespoons prepared mustard</span></li>\r\n	<li><span style=\"font-size:14px\">1 tablespoon dried minced onion</span></li>\r\n	<li><span style=\"font-size:14px\">1 tablespoon poppy seeds</span></li>\r\n	<li><span style=\"font-size:14px\">2 to 3 teaspoons sugar</span></li>\r\n</ul>', 'Baked Ham and Colby Sandwiches.jpg', '<ul>\r\n	<li><span style=\"font-size:14px\">Preheat oven to 350&deg;. In a small bowl, combine the butter, mustard, onion, poppy seeds and sugar. Place bun bottoms, cut side up, in an ungreased 15x10x1-in. baking pan. Top each with Colby cheese, ham and mozzarella. Brush with half the butter mixture.</span></li>\r\n	<li><span style=\"font-size:14px\">Replace bun tops. Brush with remaining butter mixture. Bake, uncovered, until cheese is melted, 10-15 minutes.</span></li>\r\n</ul>', 'active', '2021-01-21 03:12:39', '2021-01-21 03:19:05'),
(18, '<p><span style=\"font-size:16px\">Italian Brunch Torte Recipe</span></p>', '<ul>\r\n	<li><span style=\"font-size:14px\">2 tubes (8 ounces each) refrigerated crescent rolls, divided</span></li>\r\n	<li><span style=\"font-size:14px\">1 teaspoon olive oil</span></li>\r\n	<li><span style=\"font-size:14px\">1 package (6 ounces) fresh baby spinach</span></li>\r\n	<li><span style=\"font-size:14px\">1 cup sliced fresh mushrooms</span></li>\r\n	<li><span style=\"font-size:14px\">7 large eggs, divided use</span></li>\r\n	<li><span style=\"font-size:14px\">1 cup grated Parmesan cheese</span></li>\r\n	<li><span style=\"font-size:14px\">2 teaspoons Italian seasoning</span></li>\r\n	<li><span style=\"font-size:14px\">1/8 teaspoon pepper</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 pound thinly sliced deli ham</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 pound thinly sliced hard salami</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 pound sliced provolone cheese</span></li>\r\n	<li><span style=\"font-size:14px\">2 jars (12 ounces each) roasted sweet red peppers, drained, sliced and patted dry</span></li>\r\n</ul>', 'Italian Brunch Torte.jpg', '<ul>\r\n	<li><span style=\"font-size:14px\">Preheat oven to 350&deg;. Place a greased 9-in. springform pan on a double thickness of heavy-duty foil (about 18 in. square). Securely wrap foil around pan. Unroll 1 tube of crescent dough and separate into triangles. Press onto bottom of prepared pan to form a crust, sealing seams well. Bake until set, 10-15 minutes.</span></li>\r\n	<li><span style=\"font-size:14px\">Meanwhile, in a large skillet, heat oil over medium-high heat. Add spinach and mushrooms; cook and stir until mushrooms are tender. Drain on several layers of paper towels, blotting well. In a large bowl, whisk 6 eggs, Parmesan cheese, Italian seasoning and pepper.</span></li>\r\n	<li><span style=\"font-size:14px\">Layer crust with half of each of the following: ham, salami, provolone cheese, red peppers and spinach mixture. Pour half of the egg mixture over top. Repeat layers; top with remaining egg mixture.</span></li>\r\n	<li><span style=\"font-size:14px\">On a work surface, unroll and separate remaining crescent dough into triangles. Press together to form a circle and seal seams; place over filling. Whisk remaining egg; brush over dough.</span></li>\r\n	<li><span style=\"font-size:14px\">Bake, uncovered, until a thermometer reads 160&deg;, 1 to 1-1/4 hours, covering loosely with foil if needed to prevent overbrowning. Carefully loosen sides from pan with a knife; remove rim from pan. Let stand 20 minutes.</span></li>\r\n</ul>', 'active', '2021-01-21 03:16:37', '2021-01-21 03:19:19'),
(19, '<p><span style=\"font-size:16px\">Chicken Cordon Bleu Crescent Ring Recipe</span></p>', '<ul>\r\n	<li><span style=\"font-size:14px\">1 tube (8 ounces) refrigerated crescent rolls</span></li>\r\n	<li><span style=\"font-size:14px\">2 cups shredded Swiss cheese</span></li>\r\n	<li><span style=\"font-size:14px\">2 cups cubed cooked chicken</span></li>\r\n	<li><span style=\"font-size:14px\">3/4 cup mayonnaise</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 cup cubed fully cooked ham</span></li>\r\n	<li><span style=\"font-size:14px\">2 tablespoons honey mustard</span></li>\r\n</ul>', 'Chicken Cordon Bleu Crescent Ring.jpg', '<ol>\r\n	<li><span style=\"font-size:14px\">Preheat oven to 375&deg;. Unroll crescent dough and separate into triangles. On an ungreased 12-in. pizza pan, arrange triangles in a ring with points toward the outside and wide ends overlapping. Press overlapping dough to seal.</span></li>\r\n	<li><span style=\"font-size:14px\">In a large bowl, mix the remaining ingredients. Spoon across wide ends of triangles. Fold pointed ends of triangles over filling, tucking points under to form a ring (filling will be visible).</span></li>\r\n	<li><span style=\"font-size:14px\">Bake 15-20 minutes or until golden brown and heated through.</span></li>\r\n</ol>', 'active', '2021-01-21 03:18:44', '2021-01-21 03:18:44'),
(20, '<p><span style=\"font-size:16px\">Cuban Panini Recipe</span></p>', '<ul>\r\n	<li><span style=\"font-size:14px\">2 garlic cloves, minced</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 teaspoon olive oil</span></li>\r\n	<li><span style=\"font-size:14px\">1/2 cup reduced-fat mayonnaise</span></li>\r\n	<li><span style=\"font-size:14px\">8 slices artisan bread</span></li>\r\n	<li><span style=\"font-size:14px\">8 thick slices deli smoked turkey</span></li>\r\n	<li><span style=\"font-size:14px\">4 slices deli ham</span></li>\r\n	<li><span style=\"font-size:14px\">8 slices Swiss cheese</span></li>\r\n	<li><span style=\"font-size:14px\">12 dill pickle slices</span></li>\r\n	<li><span style=\"font-size:14px\">1 cup fresh baby spinach</span></li>\r\n</ul>', 'Cuban Panini.jpg', '<ul>\r\n	<li><span style=\"font-size:14px\">In a small skillet, cook and stir garlic in oil over medium-high heat until tender. Cool.</span></li>\r\n	<li><span style=\"font-size:14px\">Stir garlic into mayonnaise; spread over bread slices. Layer 4 slices of bread with turkey, ham, cheese, pickles and spinach; close sandwiches.</span></li>\r\n	<li><span style=\"font-size:14px\">Cook on a panini maker or indoor grill until browned and cheese is melted, 2-3 minutes.</span></li>\r\n</ul>', 'active', '2021-01-21 03:22:17', '2021-01-21 03:23:14'),
(21, '<p><span style=\"font-size:16px\">chocolate momos Recipe</span></p>', '<p><span style=\"font-size:14px\">2 cups flour</span></p>\r\n\r\n<p><span style=\"font-size:14px\">1/2 tsp salt</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Warm water, as required</span></p>\r\n\r\n<p><span style=\"font-size:14px\">3/4 cup milk or dark chocolate, chopped</span></p>', 'choclate momos.webp', '<p><span style=\"font-size:14px\">1. Take the flour and salt in a large bowl, and mix together. Gradually add the water to make a soft but not sticky dough.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">2. Divide the dough into small, equal-sized balls. Roll them out into small discs.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">3. Stuff each disc with a sizeable portion of the chopped chocolate. Put some water around the edges of each disc, and seal it completely.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">4. Steam the momos in a bamboo or steel steamer for 5-8 minutes. Serve the chocolate momos warm. Alternatively, you can also fry the momos in oil, and serve them.</span></p>', 'active', '2021-01-21 03:36:17', '2021-01-21 03:36:17'),
(22, '<p><span style=\"font-size:16px\">chilli garlic momos recipe</span></p>', '<ul>\r\n	<li><span style=\"font-size:14px\">Momos - 10&nbsp;Recipe here</span></li>\r\n	<li><span style=\"font-size:14px\">Oil - 2 tblsp + for deep frying</span></li>\r\n	<li><span style=\"font-size:14px\">Garlic - 2 tblsp chopped finely</span></li>\r\n	<li><span style=\"font-size:14px\">Onion - 1 chopped finely</span></li>\r\n	<li><span style=\"font-size:14px\">Green Chillies - 2 chopped finely</span></li>\r\n	<li><span style=\"font-size:14px\">Capsicum - 1 chopped finely</span></li>\r\n	<li><span style=\"font-size:14px\">Red Chilli Sauce - 2 tblsp</span></li>\r\n	<li><span style=\"font-size:14px\">Soy Sauce - 1 tsp</span></li>\r\n	<li><span style=\"font-size:14px\">Salt to taste</span></li>\r\n	<li><span style=\"font-size:14px\">Sugar - 1 tsp</span></li>\r\n	<li><span style=\"font-size:14px\">Cornflour - 1 tsp</span></li>\r\n	<li><span style=\"font-size:14px\">Water as needed</span></li>\r\n</ul>', 'chilli-garlic-momos-recipe.jpg', '<ul>\r\n	<li>\r\n	<p><span style=\"font-size:14px\">Take momos and deep fry lightly. Drain and set aside.</span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:14px\">Heat oil and saute garlic and onions for a min.</span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:14px\">Add in chillies and capsicum and saute for 2 mins.</span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:14px\">Add in sauces, salt, sugar and mix well.</span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:14px\">Add water and bring it to a boil.</span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:14px\">Add in cornflour slurry and mix well.</span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:14px\">Add in fried momos and mix well.</span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:14px\">Serve.</span></p>\r\n	</li>\r\n</ul>', 'active', '2021-01-21 03:40:13', '2021-01-21 03:40:13');
